import React from "react";
import {
  View,
  Image,
  ScrollView,
  Dimensions,
  Text,
  StyleSheet,
} from "react-native";

const { width } = Dimensions.get("window");
const height = width * 0.6;
const images = [
  "https://i2.wp.com/www.hashtagarabi.com/wp-content/uploads/2019/08/Screenshot_20190804-000250.png?resize=610%2C380&ssl=1",
  "data:image/jpeg;base64,",
  "https://www.orange.jo/sites/Press/EN/PublishingImages/coding-academy-entrepreneurs.jpg",
  "https://www.orange.jo/sites/Press/EN/PublishingImages/coding-academy-photo.jpg",
  "data:image/jpeg;base64,",
];
const style = StyleSheet.create({
  continue: { width, height },
  scroll: { width, height },
  image: {
    width,
    height,
    resizeMode: "cover",
  },
  pagination: {
    flexDirection: "row",
    position: "absolute",
    bottom: 0,
    alignSelf: "center",
  },
  pagingText: { fontSize: width / 30, color: "#888", margin: 3 },
  pagingActiveText: { fontSize: width / 30, color: "#fff", margin: 3 },
});

export default class Slider extends React.Component {
  state = {
    active: 0,
  };
  change = ({ nativeEvent }) => {
    const slider = Math.ceil(
      nativeEvent.contentOffset.x / nativeEvent.layoutMeasurement.width
    );
    if (slider !== this.state.active) {
      this.setState({ active: slider });
    }
  };
  render() {
    return (
      <View style={style.continue}>
        <ScrollView
          pagingEnabled
          horizontal
          onScroll={this.change}
          showsHorizontalScrollIndicator={false}
          style={style.scroll}
        >
          {images.map((image, index) => (
            <Image
              key={index}
              source={{
                uri: image,
              }}
              style={style.image}
            />
          ))}
        </ScrollView>
        <View style={style.pagination}>
          {images.map((i, k) => (
            <Text
              key={k}
              style={
                k == this.state.active
                  ? style.pagingActiveText
                  : style.pagingText
              }
            >
              â¬¤
            </Text>
          ))}
        </View>
      </View>
    );
  }
}
